    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Haie index{% endblock %}

{% block body %}
<div class=\"example-wrapper\">
    <h1>Haie index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Code</th>
                <th>Nom</th>
                <th>Prix</th>
                <th>catégorie</th>
                <th>actions</th>

            </tr>
        </thead>
        <tbody>
        {% for haie in haies %}
            <tr>
                <td>{{ haie.code }}</td>
                <td>{{ haie.nom }}</td>
                <td>{{ haie.prix }}</td>
                <td>{{ haie.categorie.libelle }}</td>
                <td>
                    <button class=\"btn btn-primary\" onclick=\"location.href ='{{ path('haie_show', {'id': haie.id}) }}'\">{{ button_label|default('Détails') }}</button>
                    <button class=\"btn btn-warning\" onclick=\"location.href ='{{ path('haie_edit', {'id': haie.id}) }}'\">{{ button_label|default('Modifier') }}</button>

                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
    <button class=\"btn btn-primary\" onclick=\"location.href ='{{ path('haie_new') }}'\">{{ button_label|default('Ajouter') }}</button>
</div>
{% endblock %}
", "haie/index.html.twig", "C:\\Users\\lucas\\PhpstormProjects\\lePtitJardinier\\templates\\haie\\index.html.twig");
